// Function to get random questions
export function getRandomTrivia(count: number): TriviaQuestion[] {
    const shuffled = [...SPACE_TRIVIA].sort(() => Math.random() - 0.5);
    return shuffled.slice(0, count);
}

// Function to shuffle and get unique questions
export function getShuffledTrivia(): TriviaQuestion[] {
    return [...SPACE_TRIVIA].sort(() => Math.random() - 0.5);
}
